const API_BASE_URL = 'http://localhost:8080';

// Fun√ß√£o para log de requisi√ß√µes (debug)
const logRequest = (method, url, data = null) => {
    console.log(`üîÑ ${method} ${url}`, data);
};

export const api = {
    async getTasks() {
        logRequest('GET', `${API_BASE_URL}/tasks`);
        try {
            const response = await fetch(`${API_BASE_URL}/tasks`);
            if (!response.ok) throw new Error(`HTTP ${response.status}: ${response.statusText}`);
            return response.json();
        } catch (error) {
            console.error('‚ùå Erro ao buscar tarefas:', error);
            throw error;
        }
    },

    async createTask(task) {
        logRequest('POST', `${API_BASE_URL}/tasks`, task);
        try {
            const response = await fetch(`${API_BASE_URL}/tasks`, {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(task)
            });
            if (!response.ok) throw new Error(`HTTP ${response.status}: ${response.statusText}`);
            return response.json();
        } catch (error) {
            console.error('‚ùå Erro ao criar tarefa:', error);
            throw error;
        }
    },

    async updateTask(id, task) {
        logRequest('PUT', `${API_BASE_URL}/tasks/${id}`, task);
        try {
            const response = await fetch(`${API_BASE_URL}/tasks/${id}`, {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(task)
            });
            if (!response.ok) throw new Error(`HTTP ${response.status}: ${response.statusText}`);
            return response.json();
        } catch (error) {
            console.error('‚ùå Erro ao atualizar tarefa:', error);
            throw error;
        }
    },

    async deleteTask(id) {
        logRequest('DELETE', `${API_BASE_URL}/tasks/${id}`);
        try {
            const response = await fetch(`${API_BASE_URL}/tasks/${id}`, {
                method: 'DELETE'
            });
            if (!response.ok) throw new Error(`HTTP ${response.status}: ${response.statusText}`);
        } catch (error) {
            console.error('‚ùå Erro ao excluir tarefa:', error);
            throw error;
        }
    }
};
